  def AccountValidators.get_phone_match(accountid)
    return /(?:\+?|\b)[0-9]{10}\b/.match(accountid)
  end

  def AccountValidators.get_fbid_match(accountid)
    return /(?<fbid_>fbid_\d{16,20})/.match(accountid)
  end

  def AccountValidators.is_test_account(accountid)
    return accountid === "vk100@onlinepay.com"
  end

  def AccountValidators.check_user_token_valid(user)
    unless(user)
      @newUser=Profile.new;
      @newUser <<
          {
              :result => 6,
              :message => "app token not valid"
          }
      respond_to do |format|
        format.json { render :signup_error, status: :unauthorized, location: profiles_url }
      end
      return;
    end
  end

  def  AccountValidators.check_app_token_valid(app)
    unless(app)
      @newUser=Profile.new;
      @newUser =
          {
              :result => 7,
              :message => "app token not valid"
          }
      respond_to do |format|
        format.json { render :signup_error, status: :unauthorized, location: profiles_url }
      end
      return;
    end
  end
